  q=exp(Pub_key,a_r,KEYDEF); // g^{r*x}
  q=inv(q,KEYDEF); // g^{-xr}
  tt=tt*q%KEYDEF; // g^{(km+xr)%mod-xr}
  m=inv(m,KEYDEF2);
  tt=exp(tt,m,KEYDEF); // g^k
  v=tt%KEYDEF2;

